import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth, logInWithEmailAndPassword } from "./../firebase";
import { useAuthState } from "react-firebase-hooks/auth";
import "./Register.css";

function Register() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [age, setAge] = useState("");
  const [height, setHeight] = useState(""); //in inches
  const [weight, setWeight] = useState("");
  const [weightGoal, setWeightGoal] = useState("");
  //TODO: add in the multiple choice for desired weight change per week over here
  const [user, loading, error] = useAuthState(auth);
  const navigate = useNavigate();

  useEffect(() => {
    if (user) {
        navigate("/home");
        console.log("go to home - user approved");
      }
  }, [user, loading]);

  return (
    <div>
      <div>
        <form onSubmit={(e) => {
              e.preventDefault();
              logInWithEmailAndPassword(email, password)
            }
          }>
          <div>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
              placeholder="First Name"
            />
          </div>
          <div>
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
            />
          </div>
          <div>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
            />
          </div>
          <button type="submit">Login</button>
          <div>
            Don't have an account? <Link to="/home">Register</Link> now.
          </div>
        </form>
      </div>
    </div>
  );
}

export default Register;
