import { useState } from "react";
import LineChart from './components/LineChart'
import { UserData } from './Data'
import {Line} from "react-chartjs-2";
import React from "react";
import "./Home.css";






function Home()
{

  // Input Values for the Graph 
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [author, setAuthor] = useState('mario');




// Weight line graph 
    const [userData, setUserData] = useState({
        labels: UserData.map((data) => data.date),
        datasets: [
          {
            label: "Weight (lbs)", 
            data: UserData.map((data) => data.weight),
            borderColor: "rgba(255, 99, 132,0.5)",
            borderWidth:2,
            backgroundColor: "rgba(255, 99, 132,0.5)"

          },

          {
            label: "Calories Consumed", 
            data: UserData.map((data) => data.caloriesConsumed),
            borderColor: "rgb(100, 200, 235,0.3)",
            borderWidth:2,
            fill: true, 
            backgroundColor: "rgb(100, 200, 235, 0.5)"
          },

          {
            label: "Calories Burned ", 
            data: UserData.map((data) => data.caloiresBurned ),
            borderColor: "rgb(53, 162, 235, 0.5)",
            borderWidth:2,
            fill: true, 
            backgroundColor:  "rgb(53, 162, 235, 0.5)"
          },


        ],
      });



    return(
      <> 
             <div className = 'Header'> 

          <h1
            className = 'title'> Welcome to Bruinfit 
            <div className = 'discription'> Take control of your life </div>
          </h1> 
        </div>

        <div style={{ width: 800 }}
        className = 'box'
        >


        <div className = 'graphTitle'>  Your Fitness Summary  </div> 
        <LineChart chartData={userData} /> 

          </div>



      <div className="create">

      <h2> Update Fitness Summary </h2>

      <form>

      <label>Date:</label>

        <select
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        >
          <option value="01"> Calories Consumed </option>
          <option value="02">Calories Burned </option>
          <option value="03"> Calories Consumed </option>
          <option value="04">Calories Burned </option>
          <option value="05"> Calories Consumed </option>
          <option value="06">Calories Burned </option>
          <option value="07"> Calories Consumed </option>
          <option value="08">Calories Burned </option>
          <option value="09"> Calories Consumed </option>
          <option value="10">Calories Burned </option>
          <option value="11"> Calories Consumed </option>
          <option value="Calories Burned">Calories Burned </option>
        </select>


        <select
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        >
          <option value="Calories Consumed "> Calories Consumed </option>
          <option value="Calories Burned">Calories Burned </option>
        </select>


        <label>Calories:</label>
        <input 
          type="text" 
          required 
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />


        <label>Weight:</label>
        <textarea
          required
          value={body}
          onChange={(e) => setBody(e.target.value)}
        ></textarea>



        <button> Add Information </button>
      </form>
    </div>




      </>

    );
}






export default Home ;
