import React, { Component } from "react";
// friends component renders each friend
const Friends  = ({friend, removeFriend}) => // friend holds all information about a friend and removeFriend removes a friend frmo their friendslist
{
    return(
        <div>
            <li className ="list-group-item">
                <strong>Name:</strong> {friend.name}
                <br />
                <strong>Age:</strong>{friend.age}
                <button onClick={(e) => {removeFriend(e,friend)}}
                    className="btn btn-danger trash">
                        <span> FaTrash0 /></span>
                    </button>
            </li>
            Friends Page!
            Friends List
        </div>
    )
}


//need a way to add friends

const API_BASE = "http://rest.learncode.academy/api/efa/friends";

//creating a friendslist
class FriendsList extends Component 
{
    constructor(props)
    {
        super(props);
        this.state = {friends: []};
    }
}


mountingData()
{
    fetch(API_BASE)
    .then(res =>res.json())
    .then(response => {
        console.log(response)
        this.ListeningStateChangedEvent({friends: response})
    })

}



export default Friends;
